letters = {}
startLetter = ""
endLetter = ""

for index, letter in enumerate(text):
    if letter in letters:
        letters[letter] += 1
    else:
        letters[letter] = 1
    if letters[letter] == 252:
        startLetter = letter
    if letters[letter] == 249:
        endLetter = letter

hiddenWord = ""
for index, value in enumerate(text):
    if text[index - 1] == startLetter and text[index + 1] == endLetter:
        hiddenWord += value
print(hiddenWord)
